import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [host, setHost] = useState('');
  const [action, setAction] = useState('');
  const [output, setOutput] = useState('');
  const [computers, setComputers] = useState([]);

  useEffect(() => {
    fetch('/api/computers')
      .then(res => res.json())
      .then(data => {
        const withAll = ['Все хосты', ...data];
        setComputers(withAll);
      })
      .catch(err => {
        console.error('Ошибка загрузки хостов:', err);
        setComputers([]);
      });
  }, []);

  const runScript = async () => {
    try {
      const res = await axios.post('http://192.170.2.212:3001/run-script', { host, action });
      setOutput(res.data);
    } catch (err) {
      setOutput(err.message);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Admin Web Tool</h1>

      <label>Выберите хост:</label>
      <select value={host} onChange={e => setHost(e.target.value)}>
        <option value="">-- Выберите хост --</option>
        {computers.map((name, idx) => (
          <option key={idx} value={name}>{name}</option>
        ))}
      </select>

      <br /><br />

      <label>update-firefox:</label>
      <input
        placeholder="Обновление Firefox"
        value={action}
        onChange={e => setAction(e.target.value)}
      />

      <br /><br />

      <button onClick={runScript}>Выполнить</button>

      <pre>{output}</pre>
    </div>
  );
}

export default App;
