import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [selectedHosts, setSelectedHosts] = useState([]);
  const [action, setAction] = useState('');
  const [output, setOutput] = useState('');
  const [computers, setComputers] = useState([]);

  useEffect(() => {
    fetch('http://192.170.2.212:3001/api/computers')
      .then(res => res.json())
      .then(data => setComputers(data))
      .catch(err => console.error('Ошибка загрузки хостов:', err));
  }, []);

  const toggleHost = (host) => {
    setSelectedHosts(prev =>
      prev.includes(host)
        ? prev.filter(h => h !== host)
        : [...prev, host]
    );
  };

  const runScript = async () => {
    try {
      const res = await axios.post('http://192.170.2.212:3001/run-script', {
        hosts: selectedHosts,
        action
      });
      setOutput(res.data);
    } catch (err) {
      setOutput(err.message);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Admin Web Tool</h1>

      <label>Выберите хост: </label>
      <div style={{ maxHeight: '200px', overflowY: 'auto', border: '1px solid #ccc', padding: '0.5rem' }}>
        {computers.map((host, idx) => (
          <div key={idx}>
            <label>
              <input
                type="checkbox"
                value={host}
                checked={selectedHosts.includes(host)}
                onChange={() => toggleHost(host)}
              />
              {host}
            </label>
          </div>
        ))}
      </div>

      <br />

      <label>update-firefox:</label>
      <input
        placeholder="Обновление Firefox"
        value={action}
        onChange={e => setAction(e.target.value)}
      />

      <br /><br />

      <button onClick={runScript} disabled={selectedHosts.length === 0 || !action}>
        Выполнить
      </button>

      <pre>{output}</pre>
    </div>
  );
}

export default App;
