import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [host, setHost] = useState('');
  const [action, setAction] = useState('');
  const [output, setOutput] = useState('');

  const runScript = async () => {
    try {
      const res = await axios.post('http://localhost:3001/run-script', { host, action });
      setOutput(res.data);
    } catch (err) {
      setOutput(err.message);
    }
  };

  return (
    <div>
      <h1>Admin Web Tool</h1>
      <input placeholder="Имя хоста" value={host} onChange={e => setHost(e.target.value)} />
      <input placeholder="Обновление Firefox" value={action} onChange={e => setAction(e.target.value)} />
      <button onClick={runScript}>Выполнить</button>
      <pre>{output}</pre>
    </div>
  );
}

export default App;
